// eslint-disable-next-line
import React, { useContext } from "react";
import axios from "axios";  
import {useEffect, useState} from "react";
import { useNavigate } from 'react-router-dom';
import ThumbUpRoundedIcon from '@mui/icons-material/ThumbUpRounded';
import { AuthContext } from "../helpers/AuthContext";

function Home() {

  const [listOfPosts, setListOfPosts] = useState([]);
  const [likedPosts, setLikedPosts] = useState([]);
  let navigate = useNavigate();
  // eslint-disable-next-line
  const { authState } = useContext(AuthContext);


  useEffect(()=>{

    if(!localStorage.getItem("accessToken")){
      navigate('/login');
    } else{
    axios.get("http://localhost:3001/posts", { headers: { accessToken: localStorage.getItem("accessToken") } }).then((response)=>{
      setListOfPosts(response.data.listOfPosts);
      setLikedPosts(response.data.likedPosts.map((like)=>{
          return like.PostId;
      })
      );
    });
    }
    // eslint-disable-next-line
  }, []);

  const likeAPost = (postId) => {
    axios.post("http://localhost:3001/likes",
      { PostId: postId },
      { headers: { accessToken: localStorage.getItem("accessToken") } }
      ).then((response) => { setListOfPosts(listOfPosts.map((post) => {
        if (post.id === postId) {
          if (response.data.liked) {
            return { ...post, Likes: [...post.Likes, 0] };
          } else {
            const likesArray = post.Likes;
            likesArray.pop();
            return { ...post, Likes: likesArray };
          }
            }else {
              return post;
            }
          })
        );
        if (likedPosts.includes(postId)){
            setLikedPosts(likedPosts.filter((id)=>{
              return id!== postId;
            })
          );
        } else {
          setLikedPosts([...likedPosts, postId])
        }
      });
  };

  return (
    <div>
      {listOfPosts.map((value, key) =>{
       return (
       <div key ={key} className='post'>
         <div className='title'> {value.title} </div>
         <div className='body'onClick={()=>{navigate(`/post/${value.id}`);}}> {value.postText} </div>
         <div className='footer'><link to={``}> {value.username} </link> <span style={{ marginRight: '240px' }}></span>
         <div className="buttons">
          <ThumbUpRoundedIcon className= {likedPosts.includes(value.id) ? "unlikeBttn": "likeBttn"} onClick={() => { likeAPost(value.id)}}/>
         </div>
         <span style={{ marginRight: '10px' }}></span>
          <label> {value.Likes.length}</label>
          </div>
        </div>
       ); 
    })}
    </div>
  );
}

export default Home;
